mapper.select:
/*프로필 작성 유무 확인*/
    SELECT
    count(*) AS countUserNo
    FROM USER_PROFILE_INFO
    WHERE USER_NO = #{userNo}

/*기존 프로필 checkProfile*/
SELECT *
FROM USER_PROFILE_INFO
WHERE USER_NO = #{userNo}

/*getProfileIdSeq --------------------------------------------------------*/
SELECT COALESCE(MAX(PROFILE_ID) + 1, 1) as profile_id_seq
FROM USER_PROFILE_INFO

/*oldPassword로 예전 비밀번호 select 쿼리*/
        SELECT password
        FROM user_info
        WHERE USER_NO = #{userNo}

mapper.update:
UPDATE user_info
SET
    password = #{password}
WHERE
    USER_NO = #{userNo}

mapper.delete:
/*job게시글 삭제하기*/
DELETE
FROM job_application_info
WHERE application_id = #{applicationId}
