mapper.select:
SELECT count(*) AS countemail
FROM user_info
WHERE email = #{email}

SELECT password
FROM user_info
WHERE email = #{email}

SELECT user_id, email, password, user_type_code, system_register_id, system_register_datetime, system_updater_id,
       system_update_datetime
FROM user_info
WHERE email = #{email}
  AND password = #{password}
